mapper.select:
select

from iam_project
where 1=1

and organization_id = #{iamProject.organizationId}



    AND name LIKE concat(concat('%',#{iamProject.name}),'%')


    AND code LIKE concat(concat('%',#{iamProject.code}),'%')


    AND is_enabled = #{iamProject.enabled}


    AND category = #{iamProject.category}
