mapper.select:
select

from iam_project
where 1=1

and organization_id = #{iamProject.organizationId}



    AND name LIKE concat(concat('%',#{iamProject.name}),'%')


    AND code LIKE concat(concat('%',#{iamProject.code}),'%')


    AND is_enabled = #{iamProject.isEnabled}


    AND category = #{iamProject.category}

SELECT DISTINCT iam_project.* FROM iam_project INNER JOIN iam_member_role ON iam_member_role.source_id = iam_project.id INNER JOIN iam_role ON iam_role.is_enabled = 1 and iam_role.id = iam_member_role.role_id AND iam_member_role.source_type = 'project' WHERE iam_member_role.member_id = #{userId} AND iam_project.organization_id = (SELECT iu.current_organization_id FROM iam_user iu WHERE iu.id = #{userId})
AND iam_project.name LIKE concat(concat('%',#{project.name}),'%')
AND iam_project.is_enabled = #{project.isEnabled}
